window.onload=()=>{let codes=document.getElementsByTagName("code");codes=[...codes];codes.forEach(code=>{code.addEventListener("click",()=>{let range=document.createRange();range.selectNode(code);window.getSelection().addRange(range);try{document.execCommand("copy");window.getSelection().removeAllRanges();showMsg("Copied!",code)}catch(err){showMsg("Unable to copy",code)}})})};const showMsg=(msg,el)=>{let msgBox=document.createElement("div");msgBox.classList.add("msg");msgBox.innerHTML=msg;el.appendChild(msgBox);setTimeout(()=>{msgBox.remove()},500)};